mapper.select:
SELECT I.IDEA_ID ,I.IDEA_NAME ,I.PLAN_ID ,P.PLAN_NAME ,I.EG_ID ,G.EG_NAME ,I.ADVERTISE_TYPE_ID ,I.UPDATE_URL ,I.IDEA_DESCRIBE ,I.IDEA_DATE ,FORMAT(SUM(IC.CLICK_NUM)/I.CLICK_RATE*100,0) AS EXHIBITS ,I.CLICK_RATE ,G.BILL AS UNIT_PRICE ,I.STATE ,SUM(IC.CLICK_NUM) AS CLICK_NUM ,I.PROD_URL ,I.CHECK_STATE ,SUM(IC.CLICK_NUM)* G.BILL AS CUSTOME FROM sevenmanager.extent_idea I LEFT JOIN sevenmanager.ideaclick IC ON IC.IDEA_ID = I.IDEA_ID LEFT JOIN sevenmanager.extent_group G ON I.EG_ID=G.EG_ID LEFT JOIN sevenmanager.plant P ON P.PLAN_ID=I.PLAN_ID
AND I.EG_ID = #{idea.egId}
AND I.PLAN_ID = #{idea.planId}
AND I.IDEA_NAME = #{idea.ideaName}
AND I.IDEA_ID = #{idea.ideaId}
AND I.CHECK_STATE = #{idea.checkState}
AND IC.IDEA_DATE >= #{idea.begDate} AND IC.IDEA_DATE < #{idea.endDate}
GROUP BY I.IDEA_ID

SELECT EI.IDEA_NAME,EI.UPDATE_URL,EI.ADVERTISE_TYPE_ID,EI.IDEA_ID,EG.EG_ID,EG.EG_NAME,P.PLAN_ID,P.PLAN_NAME ,CASE WHEN EI.CHECK_STATE=0 THEN '未通过' WHEN EI.CHECK_STATE=1 THEN '通过' ELSE '审核中' END AS CHECK_STATE ,FORMAT(SIC.CLICK_NUM/ACC.CLICK_RATE,0) AS EXHIBITS,ACC.CLICK_RATE ,AVG(EG.BILL) AS UNIT_PRICE,SIC.CLICK_NUM,SIC.CLICK_NUM*EG.BILL AS CONSUME FROM sevenmanager.account ACC LEFT JOIN sevenmanager.plant P ON P.ACC_ID=ACC.ACCOUNT_ID LEFT JOIN sevenmanager.extent_group EG ON EG.PLAN_ID=P.PLAN_ID LEFT JOIN sevenmanager.extent_idea EI ON EI.EG_ID=EG.EG_ID LEFT JOIN (SELECT IC.IDEA_ID,SUM(IC.CLICK_NUM) AS CLICK_NUM FROM sevenmanager.ideaclick IC
AND IC.IDEA_DATE >= #{idea.begDate}
AND IC.IDEA_DATE < #{idea.endDate}
GROUP BY IC.IDEA_ID) SIC ON SIC.IDEA_ID=EI.IDEA_ID
EI.IDEA_ID IS NOT NULL
AND I.IDEA_ID = #{idea.ideaId}
AND EI.CHECK_STATE = #{idea.checkState}
AND EI.IDEA_NAME LIKE "%"#{idea.ideaName}"%"
GROUP BY EI.IDEA_NAME,EI.UPDATE_URL,EI.ADVERTISE_TYPE_ID,EI.CHECK_STATE,EI.IDEA_ID,EG.EG_ID,EG.EG_NAME,P.PLAN_ID,P.PLAN_NAME,ACC.CLICK_RATE,SIC.CLICK_NUM

mapper.update:
UPDATE sevenmanager.extent_idea SET

    CHECK_STATE = #{checkState}

WHERE

    IDEA_ID = #{ideaId}

UPDATE sevenmanager.extent_idea SET

      CLICK_NUM = #{clickNum}

WHERE

    PROD_URL = #{prodUrl}

mapper.select:
SELECT EI.*,IFNULL(B.CLICK_NUM,0) clickNum,C.CLICK_RATE, FORMAT(C.AVG_BILL,2) unitPrice, FORMAT(B.CLICK_NUM / C.CLICK_RATE,2) EXHIBITS, FORMAT(B.CLICK_NUM * C.AVG_BILL,2) CONSUME,C.BALANCE FROM extent_idea EI LEFT JOIN ( SELECT IDEA_ID,SUM(IDEA.CLICK_NUM) CLICK_NUM FROM ideaclick IDEA GROUP BY IDEA.IDEA_ID ) B ON B.IDEA_ID = EI.IDEA_ID LEFT JOIN ( SELECT EG.*,A.CLICK_RATE,A.BALANCE FROM ( SELECT PLAN_ID,SUM(BILL) BILL,COUNT(*) COUNT,SUM(BILL) / COUNT(*) AVG_BILL FROM extent_group GROUP BY PLAN_ID ) EG LEFT JOIN plant P ON P.PLAN_ID = EG.PLAN_ID LEFT JOIN account A ON P.ACC_ID = A.ACCOUNT_ID ) C ON C.PLAN_ID = EI.PLAN_ID
AND EI.IDEA_DATE >= #{ideaBean.begDate}
AND EI.IDEA_DATE < #{ideaBean.endDate}
