mapper.select:
SELECT I.IDEA_ID ,I.IDEA_NAME ,I.PLAN_ID ,P.PLAN_NAME ,I.EG_ID ,G.EG_NAME ,I.ADVERTISE_TYPE_ID ,I.UPDATE_URL ,I.IDEA_DESCRIBE ,I.IDEA_DATE ,FORMAT(SUM(IC.CLICK_NUM)/I.CLICK_RATE*100,0) AS EXHIBITS ,I.CLICK_RATE ,G.BILL AS UNIT_PRICE ,I.STATE ,SUM(IC.CLICK_NUM) AS CLICK_NUM ,I.PROD_URL ,I.CHECK_STATE ,SUM(IC.CLICK_NUM)* G.BILL AS CUSTOME FROM sevenmanager.extent_idea I LEFT JOIN sevenmanager.ideaclick IC ON IC.IDEA_ID = I.IDEA_ID LEFT JOIN sevenmanager.extent_group G ON I.EG_ID=G.EG_ID LEFT JOIN sevenmanager.plant P ON P.PLAN_ID=I.PLAN_ID
AND I.EG_ID = #{idea.egId}
AND I.PLAN_ID = #{idea.planId}
AND I.IDEA_NAME = #{idea.ideaName}
AND I.IDEA_ID = #{idea.ideaId}
AND I.CHECK_STATE = #{idea.checkState}
AND IC.IDEA_DATE >= #{idea.begDate} AND IC.IDEA_DATE < #{idea.endDate}
GROUP BY I.IDEA_ID

SELECT I.IDEA_ID ,I.IDEA_NAME ,IC.IDEA_DATE ,I.PLAN_ID ,P.PLAN_NAME ,I.EG_ID ,G.EG_NAME ,I.ADVERTISE_TYPE_ID ,I.UPDATE_URL ,I.IDEA_DESCRIBE ,I.IDEA_DATE ,FORMAT(IC.CLICK_NUM/I.CLICK_RATE*100,0) AS EXHIBITS ,FORMAT(I.CLICK_RATE/100,2) AS CLICK_RATE ,G.BILL AS UNIT_PRICE ,FORMAT(I.CLICK_NUM/I.UNIT_PRICE,2) AS CUSTOME ,I.STATE ,IC.CLICK_NUM AS CLICK_NUM ,I.PROD_URL ,IC.CLICK_NUM* G.BILL AS CUSTOME FROM sevenmanager.ideaclick IC LEFT JOIN sevenmanager.extent_idea I ON IC.IDEA_ID = I.IDEA_ID LEFT JOIN sevenmanager.extent_group G ON I.EG_ID=G.EG_ID LEFT JOIN sevenmanager.plant P ON P.PLAN_ID=I.PLAN_ID
AND I.EG_ID = #{egId}
AND I.PLAN_ID = #{planId}
AND I.PROD_URL = #{prodUrl}
AND I.IDEA_ID = #{ideaId}
AND IC.IDEA_DATE >= #{begDate} AND IC.IDEA_DATE < #{endDate}
GROUP BY I.IDEA_ID, IC.IDEA_DATE

mapper.update:
UPDATE sevenmanager.extent_idea SET

    CHECK_STATE = #{checkState}

WHERE

    IDEA_ID = #{ideaId}

UPDATE sevenmanager.extent_idea SET

      CLICK_NUM = #{clickNum}

WHERE

    PROD_URL = #{prodUrl}
