mapper.select:
SELECT PLAN_ID
,PLAN_PREDICT
,STATE
,PLAN_NAME
,ACC_ID
,CREATE_DATE
FROM sevenmanager.plant



        AND PLAN_ID = #{plan.planId}


        AND ACC_ID  = #{plan.accId}


        AND CREATE_DATE >= #{plan.begDate}
        AND CREATE_DATE <= #{plan.endDate}


        --                 AND PLAN_NAME  LIKE  '% ${planName}%'
        AND PLAN_NAME  LIKE  "%"#{plan.planName}"%"

SELECT P.PLAN_ID, P.PLAN_PREDICT, P.STATE, P.PLAN_NAME, P.ACC_ID, P.CREATE_DATE,A.CLICK_RATE, FORMAT(EG.AVG_BILL,2) unitPrice,B.CLICK_NUM, FORMAT(B.CLICK_NUM / A.CLICK_RATE,2) exhibits, FORMAT(B.CLICK_NUM * EG.AVG_BILL,2) consume, A.BALANCE FROM plant P LEFT JOIN account A ON P.ACC_ID = A.ACCOUNT_ID LEFT JOIN ( SELECT PLAN_ID,SUM(BILL) BILL,COUNT(*) COUNT, SUM(BILL) / COUNT(*) AVG_BILL FROM extent_group GROUP BY PLAN_ID ) EG ON EG.PLAN_ID = P.PLAN_ID LEFT JOIN ( SELECT P.PLAN_ID,SUM(IDEA.CLICK_NUM) CLICK_NUM FROM ideaclick IDEA LEFT JOIN extent_idea EI ON EI.IDEA_ID = IDEA.IDEA_ID LEFT JOIN plant P ON P.PLAN_ID = EI.PLAN_ID GROUP BY P.PLAN_ID ) B ON P.PLAN_ID = B.PLAN_ID
AND P.CREATE_DATE >= #{plantBean.begDate}
AND P.CREATE_DATE < #{plantBean.endDate}
