mapper.select:
SELECT PLAN_ID
,PLAN_PREDICT
,STATE
,PLAN_NAME
,ACC_ID
,CREATE_DATE
FROM sevenmanager.plant



        AND PLAN_ID = #{planId}


        AND ACC_ID  = #{accId}


        AND CREATE_DATE >= #{begDate}
        AND CREATE_DATE <= #{endDate}


        --                 AND PLAN_NAME  LIKE  '% ${planName}%'
        AND PLAN_NAME  LIKE  "%"#{planName}"%"
